mapper.insert:
insert into scr_item_activity
select *,
       if(transaction_type = 'Shipped order' and left(origin_ship_name,3) = 'DOA','WR3E',
          if(transaction_type = 'Shipped order' and lower(left(po,1)) = 'q','WC3E',
             if(transaction_type = 'Shipped order'  or left(inventory_status,2) = 'AV' or left(inventory_status,2) = 'OH' or left(inventory_status,2) = 'RH' or left(inventory_status,2) = 'RT','WC1E',
                if(left(inventory_status,2) = 'CP', 'WR2E',
                    if(left(inventory_status,2) = 'SC', 'WR3E',
                        if(left(inventory_status,2) = 'DS', 'WC3E','Unknown')))))) as slot
from stg_scr_item_activity;

mapper.update:
LOAD DATA local INFILE
#{path}
INTO TABLE ${tableName}
FIELDS TERMINATED BY '\t' ENCLOSED BY '"'
LINES TERMINATED BY '\n'
IGNORE #{skip} LINES;

mapper.select:
select a.`reference`,a.item,a.transaction_type,a.transaction_date, a.po,a.inventory_status, b.mvt,b.post_date, date_format(time( date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) ,'%H:%i:%s') as `post_time`, Date(date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) as `entry_date` ,a.qty as synapse_qty,b.qty as nerp_qty,b.doc_header,b.val_type from scr_item_activity a left join scr_nerp b on a.reference = b.reference and a.item = b.material and a.slot = b.sloc where a.`reference` != '' and b.`reference` != ''
and b.sloc = #{sloc}
and a.item = #{sku} and str_to_date(`transaction_date`,'%m/%d/%Y') between #{start} and #{end} and a.qty - b.qty != 0 ;

select `material`, `plnt`, `sloc`, `mvt`, `movement_type_text`, `special_stock`, `reference`, `mat_doc`, `post_date`, date_format(time( date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) ,'%H:%i:%s') as `post_time`, Date(date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) as `entry_date`, `qty`, `username`, `doc_header`, `val_type` from scr_nerp where `reference` = ''
and sloc = #{sloc}
and material = #{sku} and str_to_date(`post_date`,'%m/%d/%Y') between #{start} and #{end} ;

select * from scr_item_activity
where `reference` = ''

        and slot = #{sloc}

    and item = #{sku}
  and str_to_date(`transaction_date`,'%m/%d/%Y') between #{start} and #{end};

select item,inventory_status as movement ,'Synapse' as source ,transaction_date as `date`,sum(qty) as qty from scr_item_activity
where `reference` = ''

         and slot = #{sloc}

    and item = #{sku} and
    str_to_date(`transaction_date`,'%m/%d/%Y') between #{start} and #{end}
group by item,inventory_status,transaction_date;

select material as item, mvt as movement,'NERP' as source,post_date as `date` ,sum(qty)  as qty from scr_nerp
where `reference` = ''

        and sloc = #{sloc}

    and material = #{sku}
    and str_to_date(`post_date`,'%m/%d/%Y') between #{start} and #{end}
group by material, mvt,post_date;

select a.* from scr_item_activity a
left join scr_nerp b
on a.`reference` = b. `reference`
and a.item = b.material
and a.slot = b.sloc
where a.`reference` != ''
and a.slot = #{sloc}
and a.item = #{sku} and str_to_date(`transaction_date`,'%m/%d/%Y') between #{start} and #{end}
and b.qty is null;

select a.`reference`,a.item,a.transaction_type,a.transaction_date, a.po,a.inventory_status, b.mvt,b.post_date, date_format(time( date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) ,'%H:%i:%s') as `post_time`, Date(date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) as `entry_date`, a.qty as synapse_qty,b.qty as nerp_qty,b.doc_header,b.val_type from scr_item_activity a left join scr_nerp b on a.reference = b.reference and a.item = b.material and a.slot = b.sloc where a.`reference` != '' and b.`reference` != ''
and b.sloc = #{sloc}
and a.item = #{sku} and str_to_date(`transaction_date`,'%m/%d/%Y') between #{start} and #{end} union select a.`reference`,a.item,a.transaction_type,a.transaction_date, a.po,a.inventory_status, b.mvt,b.post_date, date_format(time( date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) ,'%H:%i:%s') as `post_time`, Date(date_sub(timestamp(str_to_date(`entry_date`,'%m/%d/%Y'),post_time), interval 15 Hour)) as `entry_date`, a.qty as synapse_qty,b.qty as nerp_qty,b.doc_header,b.val_type from scr_item_activity a right join scr_nerp b on a.reference = b.reference and a.item = b.material and a.slot = b.sloc where a.`reference` != '' and b.`reference` != ''
and b.sloc = #{sloc}
and b.material = #{sku} and str_to_date(`post_date`,'%m/%d/%Y') between #{start} and #{end};
